SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_grava_alter_classif_corp (nr_seq_ordem_p bigint, ie_classificacao_p text, ie_classif_antiga_p text, ds_justificativa_p text, nm_usuario_p text) AS $body$
DECLARE


ds_justificativa_w		varchar(2000);
ds_classificacao_w		varchar(255);
ds_classif_antiga_w		varchar(255);
nr_seq_localizacao_w	man_localizacao.nr_sequencia%type;
cd_cnpj_w				man_localizacao.cd_cnpj%type;
nr_seq_idioma_w			pessoa_juridica.nr_seq_idioma%type;


BEGIN

select	obter_valor_dominio(1149,ie_classificacao_p),
	obter_valor_dominio(1149,ie_classif_antiga_p)
into STRICT	ds_classificacao_w,
	ds_classif_antiga_w
;



ds_justificativa_w	:= 	substr('Alteração da classificação da ordem de serviço ' ||
			'de  ' || ds_classif_antiga_w || ' para ' || ds_classificacao_w || chr(13) || chr(10) ||
			obter_desc_expressao(325773)/*'Justificativa: '*/
 || ds_justificativa_p,1,2000);

select	nr_seq_localizacao
into STRICT	nr_seq_localizacao_w
from	man_ordem_servico
where	nr_sequencia = nr_seq_ordem_p;

if (nr_seq_localizacao_w IS NOT NULL AND nr_seq_localizacao_w::text <> '') then
	select	max(a.cd_cnpj)
	into STRICT	cd_cnpj_w
	from	man_localizacao	a
	where	a.nr_sequencia	= nr_seq_localizacao_w;

	if (cd_cnpj_w IS NOT NULL AND cd_cnpj_w::text <> '') then
		select	max(a.nr_seq_idioma)
		into STRICT	nr_seq_idioma_w
		from	pessoa_juridica	a
		where	a.cd_cgc	= cd_cnpj_w;

		if (nr_seq_idioma_w IS NOT NULL AND nr_seq_idioma_w::text <> '') then
			ds_justificativa_w	:= substr(obter_texto_dic_objeto(338098, nr_seq_idioma_w, 'CLASSIFICACAO_ANTIGA='|| ds_classif_antiga_w
																						|| ';CLASSIFICACAO_NOVA=' || ds_classificacao_w
																						|| ';JUSTIFICATIVA=' || ds_justificativa_p),1,255);
		end if;
	end if;
end if;

insert into man_ordem_serv_tecnico(
	nr_sequencia,
	nr_seq_ordem_serv,
	dt_atualizacao,
	nm_usuario,
	ds_relat_tecnico,
	dt_historico,
	dt_liberacao,
	nm_usuario_lib,
	nr_seq_tipo,
	ie_origem) values (
		nextval('man_ordem_serv_tecnico_seq'),
		nr_seq_ordem_p,
		clock_timestamp(),
		nm_usuario_p,
		ds_justificativa_w,
		clock_timestamp(),
		clock_timestamp(),
		nm_usuario_p,
		1,
		'I');

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
