SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_grava_equip_parado ( nr_seq_ordem_p bigint, ie_parado_ant_p text, ie_parado_p text, nr_seq_justificativa_p bigint, ds_justificativa_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_justificativa_w			man_ordem_serv_tecnico.ds_relat_tecnico%type;
ds_parado_ant_w				varchar(255);
ds_parado_w					varchar(255);
ds_motivo_w					varchar(255);
nr_seq_tipo_w				bigint;

nr_seq_localizacao_w		man_localizacao.nr_sequencia%type;
cd_cnpj_w					man_localizacao.cd_cnpj%type;
nr_seq_idioma_w				pessoa_juridica.nr_seq_idioma%type;


BEGIN

select	CASE WHEN ie_parado_ant_p='P' THEN 'Parcialmente' WHEN ie_parado_ant_p='S' THEN 'Sim' WHEN ie_parado_ant_p='N' THEN 'N찾o'  ELSE 'Em Funcionamento' END ,
	CASE WHEN ie_parado_p='P' THEN 'Parcialmente' WHEN ie_parado_p='S' THEN 'Sim' WHEN ie_parado_p='N' THEN 'N찾o'  ELSE 'Em Funcionamento' END
into STRICT	ds_parado_ant_w,
	ds_parado_w
;

select	ds_motivo
into STRICT	ds_motivo_w
from	man_motivo_equip_parado
where	nr_sequencia = nr_seq_justificativa_p;

SELECT	(obter_valor_param_usuario(299, 280, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p))::numeric
INTO STRICT	nr_seq_tipo_w
;

ds_justificativa_w	:=	substr('Alterado status do equipamento (parado), de: ' || ds_parado_ant_w || ' para: ' || ds_parado_w || chr(13) || chr(10) ||
			obter_desc_expressao(327458)/*'Motivo: '*/
 || ds_motivo_w || chr(13) || chr(10) ||
			obter_desc_expressao(325773)/*'Justificativa: '*/
 || ds_justificativa_p,1,2000);

if (nr_seq_tipo_w in (1,2,6,129,130,132,143)) then -- Tipo de hist처ricos que foram tradados no Servidor, que v찾o para cliente (externos)
	select	nr_seq_localizacao
	into STRICT	nr_seq_localizacao_w
	from	man_ordem_servico
	where	nr_sequencia = nr_seq_ordem_p;

	if (nr_seq_localizacao_w IS NOT NULL AND nr_seq_localizacao_w::text <> '') then
		select	max(a.cd_cnpj)
		into STRICT	cd_cnpj_w
		from	man_localizacao	a
		where	a.nr_sequencia	= nr_seq_localizacao_w;

		if (cd_cnpj_w IS NOT NULL AND cd_cnpj_w::text <> '') then
			select	max(a.nr_seq_idioma)
			into STRICT	nr_seq_idioma_w
			from	pessoa_juridica	a
			where	a.cd_cgc	= cd_cnpj_w;

			if (nr_seq_idioma_w IS NOT NULL AND nr_seq_idioma_w::text <> '') then

				ds_justificativa_w	:=	substr(substr(obter_texto_dic_objeto(338215, nr_seq_idioma_w, 'DS_PARADO_ANTERIOR='||ds_parado_ant_w||';DS_PARADO_ATUAL='||ds_parado_w), 1, 255) || chr(13) || chr(10) ||
				/*Motivo: #@DS_MOTIVO#@*/
			substr(obter_texto_dic_objeto(338211, nr_seq_idioma_w, 'DS_MOTIVO='||ds_motivo_w), 1, 255) || chr(13) || chr(10) ||
				/*Justificativa: #@DS_JUSTIFICATIVA#@*/
	substr(obter_texto_dic_objeto(338217, nr_seq_idioma_w, 'DS_JUSTIFICATIVA='||ds_justificativa_p), 1, 255),1,2000);

			end if;
		end if;
	end if;
end if;

insert into man_ordem_serv_tecnico(
	nr_sequencia,
	nr_seq_ordem_serv,
	dt_atualizacao,
	nm_usuario,
	ds_relat_tecnico,
	dt_historico,
	dt_liberacao,
	nm_usuario_lib,
	ie_origem,
	nr_seq_tipo) values (
		nextval('man_ordem_serv_tecnico_seq'),
		nr_seq_ordem_p,
		clock_timestamp(),
		nm_usuario_p,
		ds_justificativa_w,
		clock_timestamp(),
		clock_timestamp(),
		nm_usuario_p,
		'I',
		nr_seq_tipo_w);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE man_grava_equip_parado ( nr_seq_ordem_p bigint, ie_parado_ant_p text, ie_parado_p text, nr_seq_justificativa_p bigint, ds_justificativa_p text, cd_estabelecimento_p bigint, nm_usuario_p text) FROM PUBLIC;

