CREATE OR REPLACE PROCEDURE man_grava_hist_reab_os ( nr_seq_ordem_p bigint, nm_usuario_encer_p text, nr_seq_tipo_hist_p bigint, ds_motivo_p text, nm_usuario_p text) AS $body$
DECLARE

 
ds_historico_w			varchar(4000);
dt_fim_real_w			varchar(20);
nm_pessoa_encer_w		varchar(255);
nm_pessoa_reab_w		varchar(255);


BEGIN 
 
select	to_char(dt_fim_real,'dd/mm/yyyy hh24:mi:ss'), 
	substr(obter_pessoa_fisica_usuario(nm_usuario_encer_p,'N'),1,255), 
	substr(obter_pessoa_fisica_usuario(nm_usuario_p,'N'),1,255) 
into STRICT	dt_fim_real_w, 
	nm_pessoa_encer_w, 
	nm_pessoa_reab_w 
from	man_ordem_servico 
where	nr_sequencia = nr_seq_ordem_p;
 
ds_historico_w	:= wheb_mensagem_pck.get_texto(348065, 	'DT_ENCERRAMENTO=' || dt_fim_real_w || 
							';NM_PESSOA=' || nm_pessoa_encer_w || 
							';NM_ENCERR=' || nm_usuario_encer_p || 
							';DT_REABERTURA=' || to_char(clock_timestamp(),'dd/mm/yyyy hh24:mi:ss') || 
							';NM_PESSOA_REAB=' || nm_pessoa_reab_w || 
							';NM_REAB=' || nm_usuario_p || 
							';DS_MOTIVO=' || ds_motivo_p);
 
/*ds_historico_w :=	'Reabertura da Ordem de serviço' || chr(10) || chr(13) || chr(10) || chr(13) || 
		'Data encerramento: ' || dt_fim_real_w || chr(10) || chr(13) || 
		'Usuário encerramento: ' || nm_pessoa_encer_w || ' (' || nm_usuario_encer_p || ')' || chr(10) || chr(13) || 
		'Data reabertura: ' || to_char(sysdate,'dd/mm/yyyy hh24:mi:ss') || chr(10) || chr(13) || 
		'Usuário reabertura: ' || nm_pessoa_reab_w || ' (' || nm_usuario_p || ')' || chr(10) || chr(13) || 
		'Motivo de reabertura da OS: ' || chr(10) || chr(13) || 
		ds_motivo_p;*/
 
 
CALL man_gravar_historico_ordem( 
		nr_seq_ordem_p, 
		clock_timestamp(), 
		ds_historico_w, 
		'I', 
		nr_seq_tipo_hist_p, 
		nm_usuario_p);
 
commit;
 
end;
$body$
