CREATE OR REPLACE PROCEDURE man_grava_servico_parada ( dt_inicial_p timestamp, dt_final_p timestamp, nr_seq_ordem_p bigint, nm_usuario_p text, nr_seq_equip_p bigint, nr_seq_motivo_p bigint) AS $body$
