CREATE OR REPLACE PROCEDURE man_inativar_planej_prev_equip ( nr_seq_equipamento_p bigint, nm_usuario_p text) AS $body$
DECLARE


c01 CURSOR FOR
SELECT	nr_sequencia
from	man_planej_prev
where	nr_seq_equip 	= nr_seq_equipamento_p
and	ie_situacao 	= 'A';

qt_existe_w	bigint;
nr_sequencia_w	bigint;


BEGIN

if (nr_seq_equipamento_p > 0) then
	select	count(*)
	into STRICT	qt_existe_w
	from	man_planej_prev
	where	nr_seq_equip 	= nr_seq_equipamento_p
	and	ie_situacao 	= 'A';

	if (qt_existe_w > 0) then
		open C01;
		loop
		fetch C01 into
			nr_sequencia_w;
		EXIT WHEN NOT FOUND; /* apply on C01 */
			begin
			update	man_planej_prev
			set	ie_situacao 	= 'I',
				nm_usuario	= nm_usuario_p,
				dt_atualizacao	= clock_timestamp()
			where	nr_sequencia 	= nr_sequencia_w;
			end;
		end loop;
		close C01;
	end if;
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
