SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_iniciar_suporte_remoto ( nr_seq_ordem_p bigint, nm_usuario_p text ) AS $body$
DECLARE

 
nm_solicitante_w		varchar(60);
dt_hora_w			varchar(10);
dt_dia_w			varchar(10);
nr_seq_tipo_w			varchar(10);
ie_mtvo_retorno_ws_w	varchar(15);


BEGIN 
 
	if (nr_seq_ordem_p IS NOT NULL AND nr_seq_ordem_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
 
		select	substr(obter_nome_pf(cd_pessoa_solicitante), 1, 60) 
		into STRICT	nm_solicitante_w 
		from	man_ordem_servico 
		where	nr_sequencia = nr_seq_ordem_p;
 
		select	substr(to_char(clock_timestamp(), 'dd/mm/yyyy hh24:mi:ss'), 12, 18) 
		into STRICT	dt_hora_w 
		;
 
		select	to_char(clock_timestamp(), 'dd/mm/yyyy') 
		into STRICT	dt_dia_w 
		;
 
		if (obter_se_base_corp = 'S') or (obter_se_base_wheb = 'S') then 
			nr_seq_tipo_w := '151';
			ie_mtvo_retorno_ws_w := '';
		else 
			nr_seq_tipo_w := obter_parametro_usuario_js(299, 200);
			ie_mtvo_retorno_ws_w := 'ASR';
		end if;
 
		insert into man_ordem_serv_tecnico(	nr_sequencia, 
				nr_seq_ordem_serv, 
				dt_atualizacao, 
				nm_usuario, 
				ds_relat_tecnico, 
				dt_historico, 
				ie_origem, 
				nr_seq_tipo, 
				dt_liberacao, 
				ie_mtvo_retorno_ws) 
		values (	nextval('man_ordem_serv_tecnico_seq'), 
				nr_seq_ordem_p, 
				clock_timestamp(), 
				nm_usuario_p, 
				/*Iniciado suporte remoto pelo cliente #@NM_SOLICITANTE#@ (#@NM_USUARIO_SOLIC#@) no dia #@DT_DIA#@ Ã s #@DT_HORA#@.*/
 
				wheb_mensagem_pck.get_texto(355324,	'NM_SOLICITANTE=' || nm_solicitante_w || ';' || 
									'NM_USUARIO_SOLIC=' || nm_usuario_p || ';' || 
									'DT_HORA=' || dt_hora_w || ';' || 
									'DT_DIA=' || dt_dia_w || ';'), 
				clock_timestamp(), 
				'I', 
				nr_seq_tipo_w, 
				clock_timestamp(), 
				ie_mtvo_retorno_ws_w);
 
		update	man_ordem_servico 
		set	ie_suporte_remoto = 'A', 
			dt_atualizacao = clock_timestamp(), 
			nm_usuario = nm_usuario_p 
		where	nr_sequencia = nr_seq_ordem_p;			
 
	end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
