SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_insere_build_git ( nr_seq_man_os_ctrl_build_p text, nm_usuario_p text, nr_ramal_p text, ds_projeto_p text, ds_arquivos_p text, ds_alteracao_p text, ie_camada_p text) AS $body$
DECLARE


nr_sequencia_w				man_os_ctrl_build_alt.nr_sequencia%type;
ds_arquivos_w				varchar(2000);
ds_classe_w					varchar(2000);

cd_pessoa_usuario_w			usuario.cd_pessoa_fisica%type;
nr_ramal_usuario_w			usuario.nr_ramal%type;


BEGIN

select	cd_pessoa_fisica,
		nr_ramal
into STRICT	cd_pessoa_usuario_w,
		nr_ramal_usuario_w
from	usuario
where	nm_usuario = nm_usuario_p;

select	max(nr_sequencia),
		max(substr(ds_classe,1,2000))
into STRICT	nr_sequencia_w,
		ds_arquivos_w
from	man_os_ctrl_build_alt
where	ds_projeto = ds_projeto_p
and		nr_seq_man_os_ctrl_build = nr_seq_man_os_ctrl_build_p;WITH RECURSIVE cte AS (


/*	a query abaixo faz o seguinte:
	. transforma o ds_arquivos_p e o ds_arquivos_w em "registros"
	. faz select destes registros com union para não repetir nomes de arquivos
	. converte os registros obtidos com o union em uma string separada por espaço */
select	substr(ltrim(ds_arquivos,' '),1,2000) ds_arquivos
into STRICT	ds_classe_w
from (
	SELECT	ds_arquivos,
			row_number() over (order by ds_arquivos) as fila
	from (WITH RECURSIVE cte AS (

		SELECT		regexp_substr(ds_arquivos_w, '[^; ]+', 1, level) ds_arquivos
		
		(regexp_substr(ds_arquivos_w, '[^; ]+', 1, level) IS NOT NULL AND (regexp_substr(ds_arquivos_w, '[^; ]+', 1, level))::text <> '')
		  UNION ALL

		SELECT		regexp_substr(ds_arquivos_w, '[^; ]+', 1, level) ds_arquivos
		
		(regexp_substr(ds_arquivos_w, '[^; ]+', 1, level) IS NOT NULL AND (regexp_substr(ds_arquivos_w, '[^; ]+', 1, level))::text <> '')
		 JOIN cte c ON ()

) SELECT * FROM cte
union

		select		regexp_substr(ds_arquivos_p, '[^; ]+', 1, level) ds_arquivos
		
		connect by	(regexp_substr(ds_arquivos_p, '[^; ]+', 1, level) IS NOT NULL AND (regexp_substr(ds_arquivos_p, '[^; ]+', 1, level))::text <> '')
	;
)
) alias16 WHERE fila = 1
  UNION ALL



select	c.ds_classe_w || ' ' || substr(ltrim(ds_arquivos,' '),1,2000) ds_arquivos
into STRICT	ds_classe_w
from (
	SELECT	ds_arquivos,
			row_number() over (order by ds_arquivos) as fila
	from (WITH RECURSIVE cte AS (

		SELECT		regexp_substr(ds_arquivos_w, '[^; ]+', 1, level) ds_arquivos
		
		(regexp_substr(ds_arquivos_w, '[^; ]+', 1, level) IS NOT NULL AND (regexp_substr(ds_arquivos_w, '[^; ]+', 1, level))::text <> '')
		  UNION ALL

		SELECT		regexp_substr(ds_arquivos_w, '[^; ]+', 1, level) ds_arquivos
		
		(regexp_substr(ds_arquivos_w, '[^; ]+', 1, level) IS NOT NULL AND (regexp_substr(ds_arquivos_w, '[^; ]+', 1, level))::text <> '')
		 JOIN cte c ON ()

) SELECT * FROM cte
union

		select		regexp_substr(ds_arquivos_p, '[^; ]+', 1, level) ds_arquivos
		
		connect by	(regexp_substr(ds_arquivos_p, '[^; ]+', 1, level) IS NOT NULL AND (regexp_substr(ds_arquivos_p, '[^; ]+', 1, level))::text <> '')
	;
)
) JOIN cte c ON (c.prior fila = alias16.(fila-1))

) SELECT * FROM cte WHERE connect_by_isleaf = 1;
;

if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
	update	man_os_ctrl_build_alt
	set		ds_classe = ds_classe_w
	where	nr_sequencia = nr_sequencia_w;
else
	insert into man_os_ctrl_build_alt(
		cd_pessoa_fisica,
		nr_seq_man_os_ctrl_build,
		nr_sequencia,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		ds_projeto,
		ds_classe,
		cd_ramal,
		ds_alteracao,
		ie_camada)
	values (
		cd_pessoa_usuario_w,
		nr_seq_man_os_ctrl_build_p,
		nextval('man_os_ctrl_build_alt_seq'),
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		ds_projeto_p,
		ds_arquivos_p,
		coalesce(nr_ramal_p,nr_ramal_usuario_w),
		CONVERT(ds_alteracao_p, 'WE8ISO8859P1', 'AL32UTF8'),
		ie_camada_p);
end if;

commit;

end;
$body$
