CREATE OR REPLACE PROCEDURE man_insere_custo_os_sol_compra ( nr_sequencia_p bigint, nr_solic_compra_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_ordem_servico_w		bigint;
vl_solic_prev_w			double precision;


BEGIN

select	sum(coalesce((coalesce(b.vl_unit_previsto,0) * b.qt_material),0))
into STRICT	vl_solic_prev_w
from	solic_compra_item b
where	b.nr_solic_compra = nr_solic_compra_p;

if (coalesce(vl_solic_prev_w,0) > 0) then
	insert into man_ordem_serv_custo(
			nr_sequencia,
			nr_seq_ordem_serv,
			dt_atualizacao,
			nm_usuario,
			dt_atualizacao_nrec,
			nm_usuario_nrec,
			ie_tipo,
			vl_custo)
		values (	nextval('man_ordem_serv_custo_seq'),
			nr_sequencia_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			'SC',
			vl_solic_prev_w);
	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
