CREATE OR REPLACE PROCEDURE man_insere_exec_padrinho ( nr_seq_ordem_p bigint, nm_usuario_p text) AS $body$
DECLARE


nm_usuario_exec_w	varchar(15);


BEGIN

if (nr_seq_ordem_p IS NOT NULL AND nr_seq_ordem_p::text <> '') then

	select	substr(obter_usuario_pessoa(a.cd_pessoa_vinculo),1,200)
	into STRICT	nm_usuario_exec_w
	from	com_canal_consultor a,
		man_ordem_servico b
	where	a.cd_pessoa_fisica = b.cd_pessoa_solicitante
	and	b.nr_sequencia = nr_seq_ordem_p;

	insert into man_ordem_servico_exec(
		nr_sequencia,
		nr_seq_ordem,
		dt_atualizacao,
		nm_usuario,
		nm_usuario_exec) values (
			nextval('man_ordem_servico_exec_seq'),
			nr_seq_ordem_p,
			clock_timestamp(),
			nm_usuario_p,
			nm_usuario_exec_w);

	commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
