CREATE OR REPLACE PROCEDURE man_insere_gerente_exec_os ( nr_seq_ordem_p bigint, nm_usuario_p text) AS $body$
DECLARE


ie_classificacao_w		varchar(15);
nr_seq_grupo_sup_w	bigint;
nr_seq_grupo_des_w	bigint;
qt_existe_w		bigint;
nm_gerente_w		varchar(15);


BEGIN

select	ie_classificacao,
	nr_seq_grupo_sup,
	nr_seq_grupo_des
into STRICT	ie_classificacao_w,
	nr_seq_grupo_sup_w,
	nr_seq_grupo_des_w
from	man_ordem_servico
where	nr_sequencia = nr_seq_ordem_p;

if (ie_classificacao_w = 'D') then
	begin

	select	max(substr(obter_usuario_pf(a.cd_responsavel),1,15))
	into STRICT	nm_gerente_w
	from	gerencia_wheb a,
		grupo_suporte b
	where	b.nr_seq_gerencia_sup = a.nr_sequencia
	and	b.nr_sequencia = nr_seq_grupo_sup_w;

	end;
elsif (ie_classificacao_w = 'S') or (ie_classificacao_w = 'T') then
	begin

	select	max(substr(obter_usuario_pf(a.cd_responsavel),1,15))
	into STRICT	nm_gerente_w
	from	gerencia_wheb a,
		grupo_desenvolvimento b
	where	b.nr_seq_gerencia = a.nr_sequencia
	and	b.nr_sequencia = nr_seq_grupo_des_w;

	end;
end if;

if (nm_gerente_w IS NOT NULL AND nm_gerente_w::text <> '') and (nm_gerente_w <> '') then
	begin

	select	count(*)
	into STRICT	qt_existe_w
	from	man_ordem_servico_exec
	where	nm_usuario_exec = nm_gerente_w
	and	nr_seq_ordem = nr_seq_ordem_p;

	if (qt_existe_w = 0) then
		begin

		insert into man_ordem_servico_exec(
			nr_sequencia,
			nr_seq_ordem,
			dt_atualizacao,
			nm_usuario,
			nm_usuario_exec,
			qt_min_prev) values (
                                          	nextval('man_ordem_servico_exec_seq'),
				nr_seq_ordem_p,
				clock_timestamp(),
				nm_usuario_p,
				nm_gerente_w,
				45);

		commit;

		end;
	end if;

	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
