SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_inserir_ativ_real_os ( nr_seq_ordem_serv_p bigint, dt_inicio_p timestamp, nr_seq_funcao_p bigint, ds_observacao_p text, nr_seq_proj_cron_etapa_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_ativ_prev_w		man_ordem_ativ_prev.nr_sequencia%type	:= null;


BEGIN

if (nr_seq_ordem_serv_p IS NOT NULL AND nr_seq_ordem_serv_p::text <> '') then
	/* Get last activity planned to SO and change the date to parameter date */

	select	max(x.nr_sequencia)
	into STRICT	nr_seq_ativ_prev_w
	from	man_ordem_ativ_prev x
	where	x.nr_seq_ordem_serv = nr_seq_ordem_serv_p
	and		x.nm_usuario_prev = nm_usuario_p
	and		dt_prevista < trunc(dt_inicio_p,'dd');

	if (coalesce(nr_seq_ativ_prev_w::text, '') = '') then
		select	max(x.nr_sequencia)
		into STRICT	nr_seq_ativ_prev_w
		from	man_ordem_ativ_prev x
		where	x.nr_seq_ordem_serv = nr_seq_ordem_serv_p
		and		x.nm_usuario_prev = nm_usuario_p
		and		dt_prevista >= trunc(dt_inicio_p,'dd');
	end if;

	if (nr_seq_ativ_prev_w IS NOT NULL AND nr_seq_ativ_prev_w::text <> '') then
		update	man_ordem_ativ_prev
		set		dt_prevista	= trunc(dt_inicio_p,'dd')
		where	nr_sequencia	= nr_seq_ativ_prev_w;
		commit;
	end if;

	insert into man_ordem_serv_ativ(nr_sequencia,
			nr_seq_ordem_serv,
			nr_seq_proj_cron_etapa,
			nm_usuario,
			dt_atualizacao,
			nm_usuario_nrec,
			dt_atualizacao_nrec,
			nm_usuario_exec,
			dt_atividade,
			nr_seq_funcao,
			qt_minuto,
			ds_observacao,
			nr_seq_ativ_prev)
		values (nextval('man_ordem_serv_ativ_seq'),
			nr_seq_ordem_serv_p,
			nr_seq_proj_cron_etapa_p,
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			clock_timestamp(),
			nm_usuario_p,
			dt_inicio_p,
			nr_seq_funcao_p,
			0,
			ds_observacao_p,
			nr_seq_ativ_prev_w);

			commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
