SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_inserir_hist_ordem_acordo ( nr_sequencia_p bigint, nm_usuario_p text, ie_tipo_p text) AS $body$
DECLARE

	ds_titulo_w		varchar(1000);
	dt_datafim_w		timestamp;
	ds_justificativa_w	varchar(4000);
	nr_seq_acordo_w		bigint;
	nr_seq_ordem_serv	bigint;
	nr_seq_tipo_hist_w	man_tipo_hist.nr_sequencia%type;

BEGIN
	select	max(ds_titulo),
		max(b.dt_entrega_desejada),
		max(b.nr_seq_acordo),
		max(b.nr_seq_ordem_servico)
	into STRICT	ds_titulo_w,
		dt_datafim_w,
		nr_seq_acordo_w,
		nr_seq_ordem_serv
	from	desenv_acordo a,
		desenv_acordo_os b
	where	a.nr_sequencia = b.nr_seq_acordo
	and	b.nr_sequencia   = nr_sequencia_p;

	if (ie_tipo_p        = 'I') then
		begin
			ds_justificativa_w := obter_desc_expressao_idioma(877804, 'Ordem de serviço adicionada no acordo', 3) ||': ' || nr_seq_acordo_w || ' - ' || ds_titulo_w || chr(10) || obter_desc_expressao_idioma(314533, 'Usuário', 3) ||':  ' || nm_usuario_p || chr(10) || obter_desc_expressao_idioma(784655, 'Data de entrega', 3) ||': ' || dt_datafim_w;
			nr_seq_tipo_hist_w := 230; -- Acordo - entrada de OS
		end;
	elsif (ie_tipo_p = 'A') then
		begin
			ds_justificativa_w := obter_desc_expressao_idioma(877858, 'Movido para o acordo', 3) ||': ' || nr_seq_acordo_w || ' - ' || ds_titulo_w || chr(10) || obter_desc_expressao_idioma(314533, 'Usuário', 3) ||':  ' || nm_usuario_p || chr(10) || obter_desc_expressao_idioma(784655, 'Data de entrega', 3) ||': ' || dt_datafim_w;
			nr_seq_tipo_hist_w := 232; -- Acordo - alteracao
		end;
	elsif (ie_tipo_p = 'E') then
		begin
			ds_justificativa_w := obter_desc_expressao_idioma(877860, 'Removido do acordo', 3) ||': ' || nr_seq_acordo_w || ' - ' || ds_titulo_w || chr(10) || obter_desc_expressao_idioma(314533, 'Usuário', 3) ||':  ' || nm_usuario_p || chr(10) || obter_desc_expressao_idioma(784655, 'Data de entrega', 3) ||': ' || dt_datafim_w;
			nr_seq_tipo_hist_w := 231; -- Acordo - exclusao de OS
		end;
	end if;
	insert
	into man_ordem_serv_tecnico(
			nr_sequencia,
			nr_seq_ordem_serv,
			dt_atualizacao,
			nm_usuario,
			ds_relat_tecnico,
			dt_historico,
			dt_liberacao,
			nm_usuario_lib,
			nr_seq_tipo,
			ie_origem
		)
		values (
			nextval('man_ordem_serv_tecnico_seq'),
			nr_seq_ordem_serv,
			clock_timestamp(),
			nm_usuario_p,
			ds_justificativa_w,
			clock_timestamp(),
			clock_timestamp(),
			nm_usuario_p,
			nr_seq_tipo_hist_w,
			'I'
		);
	commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
