CREATE OR REPLACE PROCEDURE man_inserir_usuario_executor ( nr_sequencia_p bigint, nm_usuario_exec_p text, nm_usuario_p text, qt_min_prev_p bigint default null, nr_seq_tipo_exec_p bigint default null) AS $body$
DECLARE

 
qt_existe_w			bigint;
nr_grupo_trabalho_w		man_ordem_servico.nr_grupo_trabalho%type;
nr_seq_funcao_w			man_ordem_servico_exec.nr_seq_funcao%type;


BEGIN 
if (coalesce(nm_usuario_exec_p,'X') <> 'X') then 
	begin 
	select	count(*) 
	into STRICT	qt_existe_w 
	from	man_ordem_servico_exec 
	where	nr_seq_ordem	= nr_sequencia_p 
	and	nm_usuario_exec	= nm_usuario_exec_p;
	 
	if (qt_existe_w = 0) then 
		begin 
		select	max(a.nr_grupo_trabalho) 
		into STRICT	nr_grupo_trabalho_w 
		from	man_ordem_servico	a 
		where	a.nr_sequencia	= nr_sequencia_p;
		 
		nr_seq_funcao_w	:= man_obter_funcao_ativ_exec(nr_grupo_trabalho_w, null, nm_usuario_exec_p);
		 
		if (nr_seq_funcao_w = 0) then 
			nr_seq_funcao_w	:= null;
		end if;
		 
		insert into man_ordem_servico_exec(	nr_sequencia, 
				nr_seq_ordem, 
				dt_atualizacao, 
				nm_usuario, 	 
				nm_usuario_exec, 
				qt_min_prev, 	 
				dt_ult_visao, 
				dt_recebimento, 
				nr_seq_tipo_exec, 
				nr_seq_funcao) 
		values (	nextval('man_ordem_servico_exec_seq'), 
				nr_sequencia_p, 
				clock_timestamp(), 
				nm_usuario_p, 
				nm_usuario_exec_p, 
				qt_min_prev_p, 
				null, 
				clock_timestamp(), 
				nr_seq_tipo_exec_p, 
				nr_seq_funcao_w);
				 
		commit;
		 
		end;
	end if;
	end;
end if;
 
end;
