CREATE OR REPLACE PROCEDURE man_integra_pat_local (nr_seq_localizacao_p bigint, nm_usuario_p text, ie_acao_p bigint, cd_estabelecimento_p bigint) AS $body$
DECLARE


cd_centro_custo_w		integer;
cd_setor_atendimento_w	integer;
ds_localizacao_w		varchar(80);
qt_reg_w			integer;
nr_seq_pat_local_w		bigint;
ie_situacao_w		varchar(1);
cd_classificacao_w		man_localizacao.cd_classificacao%type;
cd_localizacao_w		man_localizacao.cd_localizacao%type;
qt_metragem_w		man_localizacao.qt_metragem%type;


BEGIN

select 	cd_centro_custo,
	cd_setor,
	ds_localizacao,
	ie_situacao,
	cd_classificacao,
	cd_localizacao,
	qt_metragem
into STRICT	cd_centro_custo_w,
	cd_setor_atendimento_w,
	ds_localizacao_w,
	ie_situacao_w,
	cd_classificacao_w,
	cd_localizacao_w,
	qt_metragem_w
from 	man_localizacao
where	nr_sequencia = nr_seq_localizacao_p;

if (ie_acao_p = 1) then

	CALL man_insert_pat_local(	nm_usuario_p,
				cd_centro_custo_w,
				cd_estabelecimento_p,
				cd_setor_atendimento_w,
				ds_localizacao_w,
				nr_seq_localizacao_p);

elsif (ie_acao_p  = 2) then

	update 	pat_local
	set	cd_centro_custo = cd_centro_custo_w,
		cd_setor_atendimento = cd_setor_atendimento_w,
		ds_local = ds_localizacao_w,
		ie_situacao = ie_situacao_w,
		cd_classificacao = cd_classificacao_w,
		cd_local = cd_localizacao_w,
		qt_metragem = qt_metragem_w,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_seq_local_manutencao = nr_seq_localizacao_p;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
