CREATE OR REPLACE PROCEDURE man_liberar_analise_impacto ( nr_seq_ordem_serv_p bigint, nr_seq_impacto_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_sev_wheb_w	bigint;
nr_seq_sev_w		bigint;


BEGIN

select	coalesce(max(b.nr_seq_severidade_wheb), 0),
	coalesce(max(b.nr_seq_severidade), 0)
into STRICT	nr_seq_sev_wheb_w,
	nr_seq_sev_w
from	man_ordem_serv_impacto a,
	man_ordem_servico b
where	a.nr_seq_ordem_serv = b.nr_sequencia
and	a.nr_sequencia = nr_seq_impacto_p;

if (nr_seq_sev_wheb_w = 0) and (nr_seq_sev_w = 0) then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(1026256);
end if;

update	man_ordem_serv_impacto
set	dt_liberacao = clock_timestamp(),
	nm_usuario = nm_usuario_p
where	nr_sequencia	= nr_seq_impacto_p;


CALL man_gerar_ccb_ordem_serv(	nr_seq_ordem_serv_p,
				nr_seq_impacto_p,
				nm_usuario_p);

CALL avisar_aprovacao_ccb(nr_seq_impacto_p, 'A');

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
