SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_liberar_analise_os_filha ( nr_seq_filhas_p text, nm_usuario_p text ) AS $body$
DECLARE


c_01 CURSOR FOR
	SELECT	nr_sequencia
	from	man_ordem_serv_filha mosf
	where	mosf.nr_sequencia in (WITH RECURSIVE cte AS (

						SELECT	regexp_substr(nr_seq_filhas_p, '[^,]+', 1, level)
						
						(regexp_substr(nr_seq_filhas_p, '[^,]+', 1, level) IS NOT NULL AND (regexp_substr(nr_seq_filhas_p, '[^,]+', 1, level))::text <> '')
					  UNION ALL

						SELECT	regexp_substr(nr_seq_filhas_p, '[^,]+', 1, level) JOIN cte c ON ()

) SELECT * FROM cte;
);
BEGIN

	for reg in c_01 loop
		begin
			update	man_ordem_serv_filha
			set	dt_liberacao = clock_timestamp(),
				nm_usuario = nm_usuario_p,
				dt_atualizacao = clock_timestamp()
			where	nr_sequencia = reg.nr_sequencia;
			
		end;	
	end loop;
	commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
