SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_liberar_doc_erro ( nr_sequencia_p bigint, ie_acao_p text, nm_usuario_p text, ds_justificativa_p text, ie_forma_origem_p text, ie_libera_correcao_p text, nr_seq_motivo_testes_p bigint, ds_acao_severidade_p text) AS $body$
DECLARE


/*
ie_acao_p
L = Liberação
E = Estorno
*/
ie_situacao_w	varchar(15);
qt_existe_w	bigint;
cd_funcao_w	integer;


BEGIN

if (ie_acao_p = 'L') then
	begin
	select	f.ie_situacao,
		f.cd_funcao
	into STRICT	ie_situacao_w,
		cd_funcao_w
	from	funcao f,
		man_ordem_servico a,
		man_doc_erro b
	where	b.nr_seq_ordem 	= a.nr_sequencia
	and	f.cd_funcao 	= a.cd_funcao
	and	b.nr_sequencia 	= nr_sequencia_p;
	exception
	when others then
		ie_situacao_w := '';
	end;

	if (nr_seq_motivo_testes_p = 2) then /*Função fora do protocolo de testes, verifica se realmente não existe protocolo para a função selecionada*/
		select	count(*)
		into STRICT	qt_existe_w
		from	teste_funcao
		where	cd_funcao = cd_funcao_w
		and	ie_situacao = 'A';

		if (qt_existe_w <> 0) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(194410);
		end if;
	end if;

	update	man_doc_erro
	set	dt_liberacao 		= clock_timestamp(),
		nm_usuario		= nm_usuario_p,
		ds_justificativa		= ds_justificativa_p,
		ie_forma_origem		= ie_forma_origem_p,
		ie_libera_correcao		= ie_libera_correcao_p,
		nr_seq_motivo_testes	= nr_seq_motivo_testes_p,
		ie_situacao_funcao		= ie_situacao_w,
		ds_acao_severidade	= ds_acao_severidade_p
	where	nr_sequencia		= nr_sequencia_p;
elsif (ie_acao_p = 'E') then
	update	man_doc_erro
	set	dt_liberacao 		 = NULL,
		nm_usuario		= nm_usuario_p,
		ds_justificativa		= ds_justificativa_p,
		ie_forma_origem		= ie_forma_origem_p,
		ie_libera_correcao		= ie_libera_correcao_p,
		ie_situacao_funcao		= '',
		ds_acao_severidade	= ds_acao_severidade_p
	where	nr_sequencia		= nr_sequencia_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
