SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_liberar_historico ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE

ie_incluir_w	varchar(10);

BEGIN


if (user	= 'CORP') then

	begin
	select	coalesce(max(substr(man_obter_acesso_setor_hist(nr_seq_tipo,coalesce(obter_setor_ativo,0),'I'),1,1)),'S')
	into STRICT	ie_incluir_w
	from	man_ordem_serv_tecnico
	where	nr_sequencia= nr_sequencia_p;
	exception
		when others then
			ie_incluir_w	:= 'S';
		end;
	if (ie_incluir_w	= 'N') or (ie_incluir_w	= 'V') then
		 CALL wheb_mensagem_pck.EXIBIR_MENSAGEM_ABORT(1026116);
	end if;

end if;


update	man_ordem_serv_tecnico
set	dt_liberacao	= clock_timestamp(),
	nm_usuario_lib	= nm_usuario_p
where	nr_sequencia	= nr_sequencia_p
and	upper(nm_usuario)	= upper(nm_usuario_p)
and	coalesce(dt_liberacao::text, '') = '';

--commit;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
