CREATE OR REPLACE PROCEDURE man_liberar_hist_erro ( nr_seq_historico_p bigint, nm_usuario_liberacao_p text) AS $body$
DECLARE


nr_seq_ordem_w			bigint;
nr_seq_doc_erro_w		bigint;


BEGIN

update	pessoa_fisica_observacao
set	dt_liberacao	= clock_timestamp(),
	nm_usuario_liberacao	= nm_usuario_liberacao_p
where	nr_sequencia	= nr_seq_historico_p;

select	nr_seq_ordem,
	nr_seq_doc_erro
into STRICT	nr_seq_ordem_w,
	nr_seq_doc_erro_w
from	pessoa_fisica_observacao
where	nr_sequencia	= nr_seq_historico_p;

if (coalesce(nr_seq_ordem_w,0) > 0) then
	update	man_ordem_servico
	set	ie_orientacao_satisfacao	= 'S'
	where	nr_sequencia	= nr_seq_ordem_w;
elsif (coalesce(nr_seq_doc_erro_w,0) > 0) then
	update	man_doc_erro
	set	ie_feedback_orientacao	= 'S'
	where	nr_sequencia	= nr_seq_doc_erro_w;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
