CREATE OR REPLACE PROCEDURE man_lib_estorna_avaliacao ( nr_sequencia_p bigint, ie_opcao_p text, nm_usuario_p text) AS $body$
BEGIN

if (ie_opcao_p = 'L') then
	begin

	update	man_ordem_serv_avaliacao
	set	nm_usuario = nm_usuario_p,
		dt_liberacao = clock_timestamp()
	where	nr_sequencia = nr_sequencia_p;

	end;
elsif (ie_opcao_p = 'E') then
	begin

	update	man_ordem_serv_avaliacao
	set	nm_usuario = nm_usuario_p,
		dt_liberacao  = NULL
	where	nr_sequencia = nr_sequencia_p;

	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
