CREATE OR REPLACE PROCEDURE man_obter_dados_os ( nr_seq_ordem_serv_p INOUT bigint, ie_plataforma_p INOUT text, ie_ccb_approved_p INOUT text, nm_usuario_corp_p text ) AS $body$
DECLARE


-- Procedure utilizada pelo Dic Objetos atraves da PCK REG_OBJECT_LOG_PCK para validar as permissoes de alteracao de objetos
BEGIN

if (coalesce(nm_usuario_corp_p::text, '') = '') then

	select	a.nr_sequencia,
		a.ie_plataforma,
		man_obter_se_ccb_os_aprov(a.nr_sequencia)
	into STRICT	nr_seq_ordem_serv_p,
		ie_plataforma_p,
		ie_ccb_approved_p
	from	man_ordem_servico a,
		man_ordem_serv_ativ b
	where	a.nr_sequencia = b.nr_seq_ordem_serv
	and	a.nr_sequencia = nr_seq_ordem_serv_p  LIMIT 1;

else

	select	a.nr_sequencia,
		a.ie_plataforma,
		man_obter_se_ccb_os_aprov(a.nr_sequencia, 'N', nm_usuario_corp_p)
	into STRICT	nr_seq_ordem_serv_p,
		ie_plataforma_p,
		ie_ccb_approved_p
	from	man_ordem_servico a,
		man_ordem_serv_ativ b
	where	a.nr_sequencia = b.nr_seq_ordem_serv
	and	trunc(b.dt_atividade) = trunc(clock_timestamp())
	and	coalesce(b.dt_fim_atividade::text, '') = ''
	and	b.nm_usuario_exec = nm_usuario_corp_p  LIMIT 1;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
