CREATE OR REPLACE PROCEDURE man_ordem_atual_horas_cron ( nr_seq_ativ_os_p bigint, nr_seq_ativ_proj_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_hora_ativ_os_w	double precision;
qt_hora_prev_w		double precision;
qt_hora_real_w		double precision;
qt_hora_saldo_w		double precision;
pr_etapa_w		double precision := 0;
nr_seq_atividade_w	bigint;
nr_seq_cronograma_w	bigint;
ie_status_etapa_w	varchar(1);
nr_seq_ordem_serv_w	bigint;

BEGIN
if (nr_seq_ativ_proj_p IS NOT NULL AND nr_seq_ativ_proj_p::text <> '') then
	begin
	SELECT	dividir(coalesce(SUM(qt_minuto),0),60)
	INTO STRICT	qt_hora_real_w
	FROM	man_ordem_serv_ativ a
	WHERE	EXISTS (SELECT 1 FROM man_ordem_servico x WHERE a.nr_seq_ordem_serv = x.nr_sequencia AND x.nr_seq_proj_cron_etapa = nr_seq_ativ_proj_p);

	select	coalesce(sum(qt_hora_prev),0),
		coalesce(sum(qt_hora_saldo),0),
		coalesce(max(nr_seq_superior),0),
		coalesce(max(nr_seq_cronograma),0),
		CASE WHEN coalesce(max(ie_status_etapa),'0')='4' THEN '4'  ELSE '3' END
	into STRICT	qt_hora_prev_w,
		qt_hora_saldo_w,
		nr_seq_atividade_w,
		nr_seq_cronograma_w,
		ie_status_etapa_w
	from	proj_cron_etapa
	where	nr_sequencia = nr_seq_ativ_proj_p;

	qt_hora_saldo_w	:= qt_hora_prev_w - qt_hora_real_w;
	pr_etapa_w	:= obter_porc_utilizado(qt_hora_prev_w, qt_hora_real_w);

	update	proj_cron_etapa
	set	qt_hora_real = qt_hora_real_w,
		qt_hora_saldo = qt_hora_saldo_w,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp(),
		ie_status_etapa = 2
	where	nr_sequencia = nr_seq_ativ_proj_p;
	commit;
	CALL atualizar_horas_etapa_cron(nr_seq_ativ_proj_p);
	CALL atualizar_horas_etapa_cron(nr_seq_atividade_w);
	CALL atualizar_total_horas_cron(nr_seq_cronograma_w);

	CALL gerar_hist_ativ_os_etapa_cron(nr_seq_ativ_os_p, nr_seq_ativ_proj_p, nm_usuario_p);
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
