CREATE OR REPLACE PROCEDURE man_ordem_excluir_horas_cron ( nr_seq_ativ_os_p bigint, nr_seq_atividade_p bigint, ie_acao_p text, qt_minuto_old_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_hora_prev_w		double precision;
qt_hora_real_w		double precision;
qt_hora_saldo_w		double precision;
pr_etapa_w		double precision := 0;
qt_hora_real_ww		double precision;
nr_seq_atividade_w	bigint;
nr_seq_cronograma_w	bigint;


BEGIN
if (nr_seq_ativ_os_p IS NOT NULL AND nr_seq_ativ_os_p::text <> '') and (nr_seq_atividade_p IS NOT NULL AND nr_seq_atividade_p::text <> '') and (ie_acao_p IS NOT NULL AND ie_acao_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	if (ie_acao_p = 'E') then -- EDIT
		begin
		qt_hora_real_w := dividir(coalesce(qt_minuto_old_p,0),60);
		end;
	else
		begin
		select	dividir(coalesce(sum(qt_minuto),0),60)
		into STRICT	qt_hora_real_w
		from	man_ordem_serv_ativ
		where	nr_sequencia = nr_seq_ativ_os_p
		and	nr_seq_proj_cron_etapa = nr_seq_atividade_p;
		end;
	end if;

	select	coalesce(sum(qt_hora_real),0),
		coalesce(sum(qt_hora_prev),0),
		coalesce(sum(qt_hora_saldo),0),
		coalesce(max(nr_seq_superior),0),
		coalesce(max(nr_seq_cronograma),0)
	into STRICT	qt_hora_real_ww,
		qt_hora_prev_w,
		qt_hora_saldo_w,
		nr_seq_atividade_w,
		nr_seq_cronograma_w
	from	proj_cron_etapa
	where	nr_sequencia = nr_seq_atividade_p;

	qt_hora_real_ww	:= qt_hora_real_ww - qt_hora_real_w;
	qt_hora_saldo_w	:= qt_hora_prev_w + qt_hora_real_w;
	pr_etapa_w	:= dividir(qt_hora_real_ww, qt_hora_prev_w) * 100;

	if (pr_etapa_w > 100) then
		begin
		pr_etapa_w := 100;
		end;
	end if;

	update	proj_cron_etapa
	set	qt_hora_real = qt_hora_real_ww,
		qt_hora_saldo = qt_hora_saldo_w,
		pr_previsto = pr_etapa_w,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_seq_atividade_p;

	CALL atualizar_horas_etapa_cron(nr_seq_atividade_p);
	CALL atualizar_horas_etapa_cron(nr_seq_atividade_w);
	CALL atualizar_total_horas_cron(nr_seq_cronograma_w);
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
