SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_os_criar_entrega_build ( nr_seq_man_os_ctrl_desc_p bigint, nm_usuario_p text ) AS $body$
DECLARE


cd_versao_w			varchar(20);
cd_build_w			bigint;
nr_seq_ordem_serv_w		bigint;
ie_possui_reg_w			varchar(1);

c01 CURSOR FOR
SELECT	distinct cd_versao,
	CASE WHEN coalesce(cd_build::text, '') = '' THEN  obter_possivel_prox_build(cd_versao, nr_seq_man_os_ctrl_desc_p)  ELSE cd_build END
from	man_os_ctrl_build
where	nr_seq_man_os_ctrl_desc = nr_seq_man_os_ctrl_desc_p
order by	1;


BEGIN

if (nr_seq_man_os_ctrl_desc_p IS NOT NULL AND nr_seq_man_os_ctrl_desc_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then

	select	nr_seq_ordem_serv
	into STRICT	nr_seq_ordem_serv_w
	from	man_os_ctrl_desc
	where	nr_sequencia = nr_seq_man_os_ctrl_desc_p;

	open c01;
	loop
	fetch c01 into
		cd_versao_w,
		cd_build_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

		select	CASE WHEN count(*)=0 THEN  'N'  ELSE 'S' END
		into STRICT	ie_possui_reg_w
		from	man_ordem_serv_entrega
		where	nr_seq_ordem_serv = nr_seq_ordem_serv_w
		and	cd_versao = cd_versao_w || '.' || cd_build_w;

		if (ie_possui_reg_w = 'N') then

			insert into man_ordem_serv_entrega(
				nr_sequencia,
				dt_atualizacao,
				nm_usuario,
				dt_atualizacao_nrec,
				nm_usuario_nrec,
				nr_seq_ordem_serv,
				cd_versao,
				dt_lib_versao,
				ie_tipo_entrega)
			values (nextval('man_ordem_serv_entrega_seq'),
				clock_timestamp(),
				nm_usuario_p,
				clock_timestamp(),
				nm_usuario_p,
				nr_seq_ordem_serv_w,
				cd_versao_w || '.' || cd_build_w,
				man_obter_dia_prox_build(cd_versao_w, nr_seq_man_os_ctrl_desc_p),
				'B');

		end if;
	end;
	end loop;
	close c01;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
