CREATE OR REPLACE PROCEDURE man_os_liberar_esp_html ( nr_seq_especif_p man_os_especificacao.nr_sequencia%type, nm_usuario_p man_os_especificacao.nm_usuario%type ) AS $body$
DECLARE

	qt_requisito_caso_teste	integer;

BEGIN
	select	count(1)
	into STRICT	qt_requisito_caso_teste
	from	man_os_especif_detalhe a,
		man_os_especif_teste b,
		man_os_especif_teste_caso c
	where	a.nr_seq_especificacao = nr_seq_especif_p
	and	a.nr_seq_especificacao = b.nr_seq_especificacao
	and	b.nr_sequencia = c.nr_seq_especif_teste
	and	a.ie_tipo in ('RF', 'RNF')
	order by	1;

	if (qt_requisito_caso_teste > 0) then
		CALL man_os_especif_liberar_esp(nr_seq_especif_p, nm_usuario_p);
	else
		CALL wheb_mensagem_pck.exibir_mensagem_abort(358640);
	end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
