SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_priorizar_story ( nr_story_p bigint, nr_story_original_p bigint, nr_art_p bigint, nr_release_p bigint) AS $body$
DECLARE


nr_prioridade_original_w	desenv_story.nr_prioridade%type;
nr_prioridade_w			desenv_story.nr_prioridade%type;


BEGIN

select	coalesce(nr_prioridade,0)
into STRICT	nr_prioridade_original_w
from	desenv_story
where	nr_sequencia = nr_story_original_p;

select	coalesce(nr_prioridade,0)
into STRICT	nr_prioridade_w
from	desenv_story
where	nr_sequencia = nr_story_p;

if (nr_prioridade_original_w > nr_prioridade_w) then
	nr_prioridade_w := nr_prioridade_original_w + 1;
else
	nr_prioridade_w := nr_prioridade_original_w;
end if;

update	desenv_story
set	nr_prioridade = coalesce(nr_prioridade,0) + 1
where	coalesce(nr_prioridade,0) >= nr_prioridade_w
and	nr_sequencia in (
	SELECT	a.nr_sequencia
	FROM desenv_story a
LEFT OUTER JOIN desenv_story_sprint g ON (a.nr_sequencia = g.nr_story)
, desenv_feature b
LEFT OUTER JOIN desenv_feature_release e ON (b.nr_sequencia = e.nr_feature)
, desenv_epic d
LEFT OUTER JOIN desenv_epic_art f ON (d.nr_sequencia = f.nr_epic)
WHERE a.nr_feature = b.nr_sequencia and b.nr_epic = d.nr_sequencia    and coalesce(e.nr_release,0) = coalesce(nr_release_p,0) and coalesce(f.nr_art,0) = coalesce(nr_art_p,coalesce(f.nr_art,0)) );

update	desenv_story
set	nr_prioridade = nr_prioridade_w
where	nr_sequencia = nr_story_p;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
