CREATE OR REPLACE PROCEDURE man_proj_vincular_etapa_ordem ( nr_seq_etapa_p bigint, nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_proj_w   bigint;
ie_fase_w	proj_cron_etapa.ie_fase%type;


BEGIN
	if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
		begin
			select	coalesce(max('S'), 'N')
			into STRICT	ie_fase_w
			from	proj_cron_etapa ce
			where	ce.nr_seq_superior = nr_seq_etapa_p;
		
			if (ie_fase_w = 'S') then
				begin
					CALL wheb_mensagem_pck.exibir_mensagem_abort(1130199);
				end;
			end if;
		
			update	man_ordem_servico
			set	nr_seq_proj_cron_etapa = nr_seq_etapa_p,
				dt_atualizacao    = clock_timestamp(),
				nm_usuario        = nm_usuario_p
			where	nr_sequencia	  = nr_sequencia_p;
		
			select	max(c.nr_seq_proj)
			into STRICT	nr_seq_proj_w
			from	proj_cron_etapa t,
				proj_cronograma c
			where	t.nr_seq_cronograma = c.nr_sequencia
			and	nr_seq_etapa_p = t.nr_sequencia;
		
			insert
			into proj_ordem_servico(
					nr_sequencia,
					dt_atualizacao,
					nm_usuario,
					nr_seq_ordem,
					nr_seq_cron_etapa,
					nr_seq_proj,
					ie_origem_ordem,
					dt_atualizacao_nrec,
					nm_usuario_nrec
				)
				values (
					nextval('proj_ordem_servico_seq'),
					clock_timestamp(),
					nm_usuario_p,
					nr_sequencia_p,
					nr_seq_etapa_p,
					nr_seq_proj_w,
					'P',
					clock_timestamp(),
					nm_usuario_p
				);
			commit;
		end;
	end if;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
