SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_qua_atualizar_trein ( nm_usuario_p text, nr_seq_ordem_p bigint) AS $body$
DECLARE


nr_seq_doc_w	  bigint;


BEGIN
-- Verifica se a OS está vínculada a uma revisão do documento
select	max(nr_seq_doc)
into STRICT	nr_seq_doc_w
from	qua_doc_revisao
where	nr_seq_ordem_trein = nr_seq_ordem_p;

-- Caso seja seja um documento novo, não vai possuir revisão, e a OS vai estar vínculada ao documento
if (coalesce(nr_seq_doc_w::text, '') = '') then
	select	max(nr_sequencia)
	into STRICT	nr_seq_doc_w
	from	qua_documento
	where	nr_seq_ordem_trein = nr_seq_ordem_p;
end if;

--Agora, caso a Os esteja vínculada ao documento ou a uma revisão dele, atualiza o status
if (nr_seq_doc_w IS NOT NULL AND nr_seq_doc_w::text <> '') then
	begin
		CALL qua_atualizar_status_doc(nr_seq_doc_w, qua_doc_obter_novo_status(nr_seq_doc_w), wheb_usuario_pck.get_nm_usuario);
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
