CREATE OR REPLACE PROCEDURE man_recalcular_dif_contador (nr_seq_equipamento_p bigint) AS $body$
DECLARE


qt_contador_w		double precision := 0;
qt_contador_ant_w	double precision := 0;
nr_sequencia_w		bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia,
		qt_contador
	from	man_equip_contador
	where	nr_seq_equipamento = nr_seq_equipamento_p
	and	coalesce(dt_cancelamento::text, '') = ''
	order by dt_registro;


BEGIN

open C01;
loop
fetch C01 into
	nr_sequencia_w,
	qt_contador_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (qt_contador_w > 0) and (qt_contador_ant_w > 0) then
		update	man_equip_contador
		set	qt_diferenca	= qt_contador_w - qt_contador_ant_w
		where	nr_sequencia	= nr_sequencia_w;
	end if;
	qt_contador_ant_w := qt_contador_w;
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
