CREATE OR REPLACE PROCEDURE man_registra_lib_canc_ocorr ( ie_tipo_reg_p text, ds_mensagem_p text, nr_sequencia_p bigint, nr_seq_ordem_p bigint, nm_usuario_p text, ie_tipo_usuario_p text) AS $body$
DECLARE

ie_tipo_usuario_w		varchar(1);


BEGIN

/*
Criei o tratamento para o par√¢metro IE_TIPO_USUARIO_P, porque no JAVA
a consulta do tipo de usuario era feito no fonte. Isso pode ser feito diretamente
dentro do objeto
*/
ie_tipo_usuario_w	:= coalesce(ie_tipo_usuario_p,'X');

if (ie_tipo_usuario_w = 'X') then
	begin

	select	CASE WHEN count(*)=0 THEN 'L'  ELSE 'G' END
	into STRICT	ie_tipo_usuario_w
	from	gerencia_wheb
	where	nm_usuario = nm_usuario_p;

	end;
end if;

if (ie_tipo_reg_p = 'L') then

	update	man_ordem_serv_ocorrencia
	set 	ds_compl_lib = ds_mensagem_p,
			dt_liberacao = clock_timestamp(),
			nm_usuario_liberacao = nm_usuario_p
	where	nr_sequencia = nr_sequencia_p
	and		nr_seq_ordem_serv = nr_seq_ordem_p;

elsif (ie_tipo_reg_p = 'C') then

	if (ie_tipo_usuario_w = 'G') then

		update	man_ordem_serv_ocorrencia
		set 	ds_justif_cancel = ds_mensagem_p,
				dt_cancelamento = clock_timestamp(),
				nm_usuario_cancel = nm_usuario_p
		where	nr_sequencia = nr_sequencia_p
		and		nr_seq_ordem_serv = nr_seq_ordem_p;

	elsif (ie_tipo_usuario_w = 'L') then

		update	man_ordem_serv_ocorrencia
		set 	ds_justif_cancel = ds_mensagem_p,
				dt_cancelamento = clock_timestamp(),
				nm_usuario_cancel = nm_usuario_p
		where	nr_sequencia = nr_sequencia_p
		and		nr_seq_ordem_serv = nr_seq_ordem_p;

	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
