CREATE OR REPLACE PROCEDURE man_retornar_stories_backlog ( nm_usuario_p text) AS $body$
DECLARE


nr_sprint_w	desenv_sprint.nr_sequencia%type;
nr_story_w	desenv_story.nr_sequencia%type;
ds_erro_w	varchar(255);

c01 CURSOR FOR
	SELECT	nr_sequencia
	from	desenv_sprint
	where	trunc(dt_final,'dd') = trunc(clock_timestamp(),'dd');

c02 CURSOR FOR
	SELECT	nr_story
	from	desenv_story_sprint
	where	nr_sprint = nr_sprint_w
	and 	cd_status < 4;


BEGIN

open c01;
loop
fetch c01 into
	nr_sprint_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin

	open c02;
	loop
	fetch c02 into
		nr_story_w;
	EXIT WHEN NOT FOUND; /* apply on c02 */
		begin
		ds_erro_w := man_atribuir_story_sprint(nr_story_w, null, nr_sprint_w, nm_usuario_p, ds_erro_w);
		end;
	end loop;
	close c02;

	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
