SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_rota_define_motivo_nexec ( nr_seq_item_p bigint, nr_seq_motivo_p bigint, ds_observacao_p text, nm_usuario_p text) AS $body$
DECLARE


ie_executor_w		varchar(15);
cd_estabelecimento_w	smallint := Wheb_usuario_pck.get_cd_estabelecimento;
nm_usuario_exec_w	varchar(15) := nm_usuario_p;
nr_seq_plano_insp_w	bigint;


BEGIN

if (nr_seq_item_p > 0) and (nr_seq_motivo_p > 0) then
	ie_executor_w := obter_param_usuario(294, 13, obter_perfil_Ativo, nm_usuario_p, cd_estabelecimento_w, ie_executor_w);

	if (ie_executor_w = 'C') then
		select	b.nr_seq_plano_inspecao
		into STRICT	nr_seq_plano_insp_w
		from	man_rota_inspecao b,
			man_rota_inspecao_item a
		where	a.nr_seq_rota_inspecao 	= b.nr_sequencia
		and	a.nr_sequencia		= nr_seq_item_p;

		select	max(nm_usuario_exec)
		into STRICT	nm_usuario_exec_w
		from	man_plano_insp_exec
		where	nr_seq_plano_inspecao = nr_seq_plano_insp_w;
	end if;

	update	man_rota_inspecao_item
	set	nr_seq_motivo_nexec 	= nr_seq_motivo_p,
		dt_atualizacao		= clock_timestamp(),
		nm_usuario		= nm_usuario_p,
		ds_observacao		= ds_observacao_p,
		ie_status			= 'S',
		dt_verificacao		= clock_timestamp(),
		nm_usuario_exec		= coalesce(nm_usuario_exec_w,nm_usuario_p)
	where	nr_sequencia		= nr_seq_item_p;

	commit;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
