CREATE OR REPLACE PROCEDURE man_solicitar_suporte_remoto ( nr_seq_ordem_p bigint, nm_usuario_p text, ds_observacoes_p text ) AS $body$
BEGIN
 
	if (nr_seq_ordem_p IS NOT NULL AND nr_seq_ordem_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
 
		insert into man_ordem_serv_tecnico(	nr_sequencia, 
				nr_seq_ordem_serv, 
				dt_atualizacao, 
				nm_usuario, 
				ds_relat_tecnico, 
				dt_historico, 
				ie_origem, 
				nr_seq_tipo, 
				dt_liberacao) 
		values	(	nextval('man_ordem_serv_tecnico_seq'), 
				nr_seq_ordem_p, 
				clock_timestamp(), 
				nm_usuario_p, 
				--O colaborador #@NM_PESSOA_FISICA#@ detectou a necessidade de conexão remota ao cliente para esta atividade. 
				(wheb_mensagem_pck.get_texto(355323, 'NM_PESSOA_FISICA=' || substr(obter_pessoa_fisica_usuario(wheb_usuario_pck.get_nm_usuario, 'N'), 1, 80)) || chr(10) || chr(13) || chr(10) || chr(13) || 
				ds_observacoes_p), 
				clock_timestamp(), 
				'I', 
				149, --Suporte remoto - Solicitação 
				clock_timestamp());
 
		update	man_ordem_servico 
		set	ie_suporte_remoto = 'S', 
			dt_atualizacao = clock_timestamp(), 
			nm_usuario = nm_usuario_p 
		where	nr_sequencia = nr_seq_ordem_p;
 
	end if;
 
commit;
 
end;
$body$
