SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_valida_tempo_analise () AS $body$
DECLARE


nr_seq_ativ_real_w	bigint;
nr_seq_ordem_w		bigint;

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	man_ordem_servico a,
		man_ordem_serv_ativ b
	where	a.nr_sequencia = b.nr_seq_ordem_serv
	and	a.nr_seq_estagio = 731
	and	b.dt_atividade <= clock_timestamp() - interval '5' day
	and	b.nr_seq_funcao = 31
	and	b.dt_atividade = (	SELECT	max(c.dt_atividade)
					from	man_ordem_serv_ativ c
					where	a.nr_sequencia = c.nr_seq_ordem_serv
					and	c.nr_seq_funcao = 31);


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_ordem_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	update	man_ordem_servico
	set	nr_seq_estagio = 4
	where	nr_sequencia = nr_seq_ordem_w;

	insert into man_ordem_serv_tecnico(	nr_sequencia,
						nr_seq_ordem_serv,
						dt_atualizacao,
						dt_historico,
						dt_liberacao,
						nm_usuario,
						nm_usuario_lib,
						ie_origem,
						nr_seq_tipo,
						ds_relat_tecnico)
					values (	nextval('man_ordem_serv_tecnico_seq'),
						nr_seq_ordem_w,
						clock_timestamp(),
						clock_timestamp(),
						clock_timestamp(),
						'Tasy',
						'Tasy',
						'I',
						7, /*DiÃ¡logo interno*/
						wheb_mensagem_pck.get_texto(334701));
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
