SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_vincular_os_doc_erro ( nr_seq_ordem_p bigint, nr_seq_ordem_orig_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w		varchar(10);
ie_origem_erro_w	varchar(1);
ie_terceiro_w		varchar(10);
ie_ident_erro_w		varchar(10) := null;
ie_base_cliente_w	varchar(10) := null;


BEGIN

select	max(IE_ORIGEM_ERRO)
into STRICT	ie_origem_erro_w
from	man_doc_erro
where	nr_seq_ordem = nr_seq_ordem_orig_p;

select	coalesce(ie_terceiro,'N')
into STRICT	ie_terceiro_w
from	man_localizacao b,
	man_ordem_servico a
where	a.nr_seq_localizacao	= b.nr_sequencia
and	a.nr_sequencia		= nr_seq_ordem_p;

if (ie_terceiro_w = 'N') then
	ie_ident_erro_w		:= 'W';
	ie_base_cliente_w	:= 'T';
end if;

if (ie_origem_erro_w = 'C') then
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(262005); -- A origem do erro é do cliente não pode ser vinculado
end if;


if (nr_seq_ordem_p IS NOT NULL AND nr_seq_ordem_p::text <> '') and (nr_seq_ordem_orig_p IS NOT NULL AND nr_seq_ordem_orig_p::text <> '') then
	begin

	select	max(nr_sequencia)
	into STRICT	nr_sequencia_w
	from	man_doc_erro
	where	nr_seq_ordem = nr_seq_ordem_orig_p;

	insert into man_doc_erro(
			nr_sequencia,
			ie_origem_erro,
			nr_seq_grupo_des,
			ie_gravidade,
			nr_seq_tipo,
			nr_seq_subtipo,
			nr_seq_classif,
			cd_pessoa_fisica,
			cd_pessoa_resp,
			qt_cliente,
			ie_recorrente,
			dt_ocorrencia_erro,
			ds_acao_imediata,
			ds_solucao,
			ie_ident_erro,
			nr_seq_ordem_origem,
			dt_atualizacao,
			dt_atualizacao_nrec,
			nm_usuario,
			nm_usuario_nrec,
			nr_seq_ordem,
			dt_liberacao,
			nm_usuario_liberou_erro,
			ie_forma_origem,
			ie_libera_correcao,
			nr_seq_lp,
			ie_base_cliente)
		SELECT	nextval('man_doc_erro_seq'),
			ie_origem_erro,
			nr_seq_grupo_des,
			ie_gravidade,
			nr_seq_tipo,
			nr_seq_subtipo,
			nr_seq_classif,
			cd_pessoa_fisica,
			cd_pessoa_resp,
			1,
			'S',
			dt_ocorrencia_erro,
			ds_acao_imediata,
			ds_solucao,
			coalesce(ie_ident_erro_w,ie_ident_erro),
			nr_seq_ordem_orig_p,
			clock_timestamp(),
			clock_timestamp(),
			nm_usuario_p,
			nm_usuario_p,
			nr_seq_ordem_p,
			clock_timestamp(),
			nm_usuario_p,
			'9',
			ie_libera_correcao,
			nr_seq_lp,
			coalesce(ie_base_cliente_w,ie_base_cliente)
		from	man_doc_erro
		where	nr_sequencia = nr_sequencia_w;

		update	man_doc_erro
		set	qt_cliente 	= coalesce(qt_cliente,1) + 1
		where	nr_sequencia 	= nr_sequencia_w;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
