CREATE OR REPLACE PROCEDURE man_vincular_os_proj_projeto ( nr_seq_projeto_p bigint, nr_seq_ordem_p bigint, nm_usuario_p text) AS $body$
DECLARE



nr_seq_ordem_serv_w	bigint;
qt_existe_os_w		bigint;
ds_titulo_w		varchar(80);
ds_relat_tecnico_w	text;


BEGIN

select	count(*)
into STRICT	qt_existe_os_w
from	man_ordem_servico
where	nr_sequencia	= nr_seq_ordem_p;

if (qt_existe_os_w = 0) then
	/*(-20011,'Não existe uma ordem de serviço com este número, verifique.');*/

	CALL wheb_mensagem_pck.exibir_mensagem_abort(263165);
end if;

select	nr_seq_ordem_serv
into STRICT	nr_seq_ordem_serv_w
from	proj_projeto
where	nr_sequencia	= nr_seq_projeto_p;

select	count(*)
into STRICT	qt_existe_os_w
from	proj_projeto
where	nr_seq_ordem_serv = nr_seq_ordem_p;

if (coalesce(nr_seq_ordem_serv_w,0) > 0) or (qt_existe_os_w > 0) then
	/*(-20011,'O projeto já possui uma ordem de serviço vinculada à ele, ou a ordem de serviço já está vinculada a outro projeto.');*/

	CALL wheb_mensagem_pck.exibir_mensagem_abort(263166);
end if;

update	proj_projeto
set	nr_seq_ordem_serv		= nr_seq_ordem_p,
	dt_atualizacao		= clock_timestamp(),
	nm_usuario		= nm_usuario_p
where	nr_sequencia		= nr_seq_projeto_p;

select	ds_titulo
into STRICT	ds_titulo_w
from	proj_projeto
where	nr_sequencia	= nr_seq_projeto_p;

ds_relat_tecnico_w	:= wheb_mensagem_pck.get_texto(303189,'DS_TITULO=' || ds_titulo_w);

insert into man_ordem_serv_tecnico(
	nr_sequencia,
	nr_seq_ordem_serv,
	dt_atualizacao,
	nm_usuario,
	ds_relat_tecnico,
	dt_historico,
	ie_origem,
	dt_liberacao,
	nm_usuario_lib)
values (	nextval('man_ordem_serv_tecnico_seq'),
	nr_seq_ordem_p,
	clock_timestamp(),
	nm_usuario_p,
	ds_relat_tecnico_w,
	clock_timestamp(),
	'I',
	clock_timestamp(),
	nm_usuario_p);
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
