CREATE OR REPLACE PROCEDURE man_vincular_plano_cont (nr_seq_plano_p bigint, nr_seq_ordem_p bigint, nm_usuario_p text) AS $body$
DECLARE



ds_atividade_w	varchar(2000);
qt_min_prev_w	bigint;

C01 CURSOR FOR
	SELECT	ds_atividade,
		qt_min_prev
	from	man_plano_cont_ativ_prev
	where	nr_seq_plano = nr_seq_plano_p
	order by nr_sequencia;

BEGIN

update	man_ordem_servico
set	nr_seq_plano_cont = CASE WHEN coalesce(nr_seq_plano_p,0)=0 THEN null  ELSE nr_seq_plano_p END ,
	nm_usuario	  = nm_usuario_p,
	dt_atualizacao    = clock_timestamp()
where	nr_sequencia	= nr_seq_ordem_p;

open C01;
loop
fetch C01 into
	ds_atividade_w,
	qt_min_prev_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	if (coalesce(nr_seq_plano_p,0) > 0) then
		insert into man_ordem_ativ_prev(
			nr_sequencia,
			nr_seq_ordem_serv,
			nm_usuario,
			dt_atualizacao,
			ds_atividade,
			qt_min_prev)
		values (
			nextval('man_ordem_ativ_prev_seq'),
			nr_seq_ordem_p,
			nm_usuario_p,
			clock_timestamp(),
			ds_atividade_w,
			qt_min_prev_w
			);
	end if;
	end;
end loop;
close C01;

commit;

end;
$body$
