SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE man_vincular_story_impedimento (nr_story_p bigint, nr_impedimento_p bigint, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE


qt_existe_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	desenv_story
where	nr_sequencia = nr_impedimento_p;

if (qt_existe_w = 0) then
	ds_erro_p := wheb_mensagem_pck.get_texto(388893);
	return;
end if;

select	count(*)
into STRICT	qt_existe_w
from	desenv_story_impedimento
where	nr_story = nr_story_p
and	nr_impedimento = nr_impedimento_p;

if (qt_existe_w = 0) then
	insert into desenv_story_impedimento(
		dt_atualizacao,
		dt_atualizacao_nrec,
		nm_usuario,
		nm_usuario_nrec,
		nr_impedimento,
		nr_sequencia,
		nr_story)
	values (	clock_timestamp(),
		clock_timestamp(),
		nm_usuario_p,
		nm_usuario_p,
		nr_impedimento_p,
		nextval('desenv_story_impedimento_seq'),
		nr_story_p);

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
