SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE marcar_agenda_consulta (nr_seq_agenda_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
nr_sequencia_w		bigint;
hr_inicio_w		timestamp;
cd_convenio_w		integer;
cd_pessoa_fisica_w	varchar(10);
nr_minuto_duracao_w	bigint;	
nr_seq_age_cons_w	bigint;	
cd_agenda_cons_w	bigint;
cd_medico_w		varchar(10);
cd_estabelecimento_w	bigint;
dt_agenda_w		timestamp;


BEGIN 
 
select	a.cd_pessoa_fisica, 
	a.hr_inicio, 
	a.cd_convenio, 
	a.nr_minuto_duracao, 
	a.cd_medico, 
	coalesce(a.nr_seq_age_cons,0), 
	b.cd_estabelecimento 
into STRICT	cd_pessoa_fisica_w, 
	hr_inicio_w, 
	cd_convenio_w, 
	nr_minuto_duracao_w, 
	cd_medico_w, 
	nr_seq_age_cons_w, 
	cd_estabelecimento_w 
from	agenda b, agenda_paciente a 
where	a.cd_agenda	= b.cd_agenda 
and	a.nr_sequencia	= nr_seq_agenda_p;
 
if (nr_seq_age_cons_w = 0) then 
	begin 
 
	select	coalesce(max(a.cd_agenda),0) 
	into STRICT	cd_agenda_cons_w 
	FROM pessoa_fisica b, agenda a
LEFT OUTER JOIN medico c ON (a.cd_pessoa_fisica = c.cd_pessoa_fisica)
WHERE a.cd_pessoa_fisica  = b.cd_pessoa_fisica  and a.cd_pessoa_fisica  = cd_medico_w and a.cd_estabelecimento = cd_estabelecimento_w and coalesce(a.ie_situacao,'A') = 'A' and cd_tipo_agenda    = 3;
 
	if (cd_agenda_cons_w > 0) then 
		begin 
 
		select	coalesce(max(nr_sequencia),0), 
			max(dt_agenda) 
		into STRICT	nr_sequencia_w, 
			dt_agenda_w 
		from	agenda_consulta 
		where	cd_agenda	= cd_agenda_cons_w 
		and	trunc(dt_agenda, 'mi')	= trunc(hr_inicio_w, 'mi') 
		and	ie_status_agenda <> 'L';	
 
		select	nextval('agenda_consulta_seq') 
		into STRICT	nr_sequencia_w 
		;
 
		insert 	into agenda_consulta(NR_SEQUENCIA      , 
			CD_AGENDA       , 
			DT_AGENDA       , 
			NR_MINUTO_DURACAO   , 
			IE_STATUS_AGENDA    , 
			IE_CLASSIF_AGENDA   , 
			DT_ATUALIZACAO     ,	 
			NM_USUARIO       , 
			CD_CONVENIO      , 
			CD_PESSOA_FISICA    , 
			NM_PESSOA_CONTATO   , 
			DS_OBSERVACAO     , 
			IE_STATUS_PACIENTE   , 
			NR_SEQ_CONSULTA    , 
			NM_PACIENTE      , 
			NR_ATENDIMENTO     , 
			DT_CONFIRMACAO     , 
			DS_CONFIRMACAO     , 
			NR_TELEFONE      , 
			QT_IDADE_PAC      , 
			NR_SEQ_PLANO      , 
			NR_SEQ_CLASSIF_MED   , 
			NM_USUARIO_ORIGEM   , 
			IE_NECESSITA_CONTATO, 
			cd_turno  ) 
		values (nr_sequencia_w, 
			cd_agenda_cons_w, 
			CASE WHEN nr_sequencia_w=0 THEN  hr_inicio_w  ELSE dt_agenda_w + 1/86400 END , 
			nr_minuto_duracao_w, 
			'N', 'C', 
			clock_timestamp(), nm_usuario_p, 
			cd_convenio_w, 
			cd_pessoa_fisica_w, 
			null, null, 'N', 
			null, null, null, null, 
			null, null, null, null, 
			null, null, 'N', 0);
 
		update	agenda_paciente 
		set	nr_seq_age_cons = nr_sequencia_w 
		where	nr_sequencia	= nr_seq_agenda_p;
		end;
	end if;	
	 
	end;
end if;	
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
