SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE marcar_analisar_protocolo ( nm_usuario_p text, ie_analisada_p text, nr_seq_ret_item_p bigint, nr_seq_protocolo_p bigint, nr_seq_retorno_p bigint, permite_analizar_p text) AS $body$
BEGIN
if (IE_ANALISADA_P = 'N') then 
	begin 
	update	convenio_retorno_item a 
	set	dt_analisada = clock_timestamp(), 
		ie_analisada = 'S' 
	where	1 = 1 
	and   a.nr_sequencia  in (	SELECT b.nr_sequencia 
					from	convenio_retorno_item b, 
						conta_paciente a 
					where  b.nr_sequencia    	<> NR_SEQ_RET_ITEM_P 
					and   a.nr_interno_conta  	= b.nr_interno_conta 
					and   a.nr_seq_protocolo  	= NR_SEQ_PROTOCOLO_P) 
	and   a.nr_seq_retorno 	= NR_SEQ_RETORNO_P 
	and	(((permite_analizar_p = 'H') and 
		((coalesce(a.vl_amenor,0) = 0) or (a.ie_analisada = 'S') or (not exists (	select 1 
				from   convenio_retorno_glosa x 
				where  coalesce(x.nr_seq_conpaci_ret_hist::text, '') = '' 
				and   x.nr_seq_ret_item     = a.nr_sequencia)))) or (permite_analizar_p = 'S')) 
	and	(((permite_analizar_p = 'R') and 
		((coalesce(vl_amenor,0) = 0) or (ie_analisada = 'S'))) or (permite_analizar_p = 'S'));
	end;
	commit;		
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
