SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE marcar_comunic_lida (nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


qt_registro_w	bigint;


BEGIN

select	count(*)
into STRICT	qt_registro_w
from	comunic_interna_lida
where	nr_sequencia = nr_sequencia_p
and	nm_usuario = nm_usuario_p;

if (qt_registro_w = 0) then
	begin
	insert into comunic_interna_lida(
			nr_sequencia,
			nm_usuario,
			dt_atualizacao)
		values (nr_sequencia_p,
			nm_usuario_p,
			clock_timestamp());

	update	alerta_tasy_ocor
	set	nm_usuario_ciente	= nm_usuario_p,
		dt_usuario_ciente	= clock_timestamp(),
		ie_usuario_ciente	= 'S'
	where	nr_seq_comunic		= nr_sequencia_p;

	commit;
	end;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
