CREATE OR REPLACE PROCEDURE marcar_guia_reap_tiss ( nr_sequencia_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_prot_guia_w	tiss_conta_guia.nr_seq_prot_guia%type;


BEGIN

	update	tiss_conta_guia set
		ie_reapresentacao = 'S',
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_sequencia_p
	returning	nr_seq_prot_guia into nr_seq_prot_guia_w;

	update	tiss_protocolo_guia set
		ie_reapresentacao = 'S',
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_seq_prot_guia_w
	and	coalesce(ie_reapresentacao,'N') = 'N';

	commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
