CREATE OR REPLACE PROCEDURE marcar_item_auditado ( nr_sequencia_p bigint, nm_tabela_p text, nr_seq_motivo_p bigint, nm_usuario_p text) AS $body$
BEGIN

if (nm_tabela_p IS NOT NULL AND nm_tabela_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	begin
	if (upper(nm_tabela_p) = 'AUDITORIA_MATPACI') then
		begin
		update	auditoria_matpaci
		set	ie_tipo_auditoria	= 'D',
			nm_usuario	= nm_usuario_p,
			nr_seq_motivo	= nr_seq_motivo_p,
			dt_atualizacao	= clock_timestamp()
		where 	nr_sequencia 	= nr_sequencia_p;
		end;
	elsif (upper(nm_tabela_p) = 'AUDITORIA_PROPACI') then
		begin
		update 	auditoria_propaci
		set	ie_tipo_auditoria	= 'D',
			nm_usuario	= nm_usuario_p,
			nr_seq_motivo	= nr_seq_motivo_p,
			dt_atualizacao	= clock_timestamp()
		where 	nr_sequencia 	= nr_sequencia_p;
		end;
	elsif (upper(nm_tabela_p) = 'AUDITORIA_EXTERNA')then
		begin
		update 	auditoria_externa
		set	ie_auditado	= 'S',
			nm_usuario	= nm_usuario_p,
			nr_seq_motivo	= nr_seq_motivo_p,
			dt_atualizacao	= clock_timestamp()
		where 	nr_sequencia 	= nr_sequencia_p;
		end;
	end if;
	end;
end if;

commit;

end;
$body$
