CREATE OR REPLACE PROCEDURE marcar_item_como_auditado ( ie_tipo_item_p text, nr_seq_item_p bigint, nr_seq_motivo_p bigint) AS $body$
BEGIN

if (ie_tipo_item_p IS NOT NULL AND ie_tipo_item_p::text <> '') and (nr_seq_item_p IS NOT NULL AND nr_seq_item_p::text <> '') then
	begin
	if (ie_tipo_item_p = 2) then
		begin
		update	auditoria_matpaci
		set	ie_tipo_auditoria	= 'D',
			nr_seq_motivo	= CASE WHEN coalesce(nr_seq_motivo_p, 0) =0 THEN  nr_seq_motivo  ELSE nr_seq_motivo_p END ,
			dt_atualizacao	= clock_timestamp()
		where 	nr_sequencia 	= nr_seq_item_p;
		end;
	elsif (ie_tipo_item_p = 1) then
		begin
		update 	auditoria_propaci
		set	ie_tipo_auditoria	= 'D',
			nr_seq_motivo	= CASE WHEN coalesce(nr_seq_motivo_p, 0) =0 THEN  nr_seq_motivo  ELSE nr_seq_motivo_p END ,
			dt_atualizacao	= clock_timestamp()
		where 	nr_sequencia 	= nr_seq_item_p;
		end;
	end if;
	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
