SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE marcar_sub_idiomas_lidos ( ie_doc_resp_p text, nr_seq_doc_p bigint, nm_usuario_p text ) AS $body$
DECLARE


nr_seq_doc_w		bigint;
nr_seq_idioma_w		bigint;
nr_seq_superior_w	bigint;

c01 CURSOR FOR
	SELECT	nr_sequencia
	from	qua_documento
	where	((nr_sequencia = nr_seq_superior_w) and (coalesce(nr_seq_superior::text, '') = ''))
	or	((nr_seq_superior = nr_seq_superior_w) or ((coalesce(nr_seq_superior_w::text, '') = '') and (nr_seq_superior = nr_seq_doc_p)))
	and (nr_sequencia <> nr_seq_doc_p);


BEGIN
	select	max(nr_seq_idioma),
		max(nr_seq_superior)
	into STRICT	nr_seq_idioma_w,
		nr_seq_superior_w
	from	qua_documento
	where	nr_sequencia = nr_seq_doc_p;

	if (nr_seq_idioma_w = philips_param_pck.get_nr_seq_idioma) then
		open c01;
		loop
		fetch c01 into
			nr_seq_doc_w;
		EXIT WHEN NOT FOUND; /* apply on c01 */
			begin
				update	qua_doc_log_acesso
				set	dt_fim_leitura = clock_timestamp(),
					dt_leitura = clock_timestamp(),
					dt_atualizacao = clock_timestamp(),
					ie_doc_resp = ie_doc_resp_p
				where	nr_seq_doc = nr_seq_doc_w
				and	nm_usuario = nm_usuario_p;
			end;
		end loop;
		close c01;
	end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
