CREATE OR REPLACE PROCEDURE matrix_alterar_status_ws ( nr_prescricao_p bigint, nr_seq_prescricao_p text, ie_status_p bigint default 35) AS $body$
DECLARE


nr_seq_exame_w		bigint;


BEGIN

/*select	max(a.nr_seq_exame)
into	nr_seq_exame_w
from	lab_exame_equip a,
		equipamento_lab b
where	a.cd_equipamento = b.cd_equipamento
AND	b.ds_sigla = 'MATRIX'
and	a.cd_exame_equip = nr_seq_exame_p;

select	max(a.nr_sequencia)
into	nr_seq_prescr_w
from	prescr_procedimento a
where	a.nr_prescricao = nr_prescricao_p
and		a.nr_seq_exame = nr_seq_exame_p;*/
update	exame_lab_result_item
set		dt_aprovacao = case when ie_status_p >= 35 then clock_timestamp() else null end
where	nr_seq_resultado = (SELECT max(x.nr_seq_resultado) from exame_lab_resultado x where x.nr_prescricao = nr_prescricao_p)
and		nr_seq_prescr = nr_seq_prescricao_p
and		(nr_seq_material IS NOT NULL AND nr_seq_material::text <> '');

update	prescr_procedimento
set		ie_status_atend = coalesce(ie_status_p, 35)
where	nr_prescricao = nr_prescricao_p
and		nr_sequencia = nr_seq_prescricao_p
and		ie_status_atend <> coalesce(ie_status_p, 35);

commit;

end;
$body$
