CREATE OR REPLACE PROCEDURE mdc_atualizar_fim_ventilacao (nr_sequencia_p bigint, dt_fim_p timestamp, nm_usuario_p text, ds_erro_p INOUT text) AS $body$
DECLARE


ds_erro_w		            varchar(4000);
dt_inicio_ventilacao_w	    timestamp;
qt_horas_total_w	        mdc_episodio_pac_resp_mec.qt_horas_total%type;

BEGIN

select	mdc_itervalo_vm_valido(nm_usuario_p,nr_seq_episodio, dt_inicio_ventilacao, dt_fim_p, nr_sequencia_p),
	dt_inicio_ventilacao
into STRICT	ds_erro_w,
	dt_inicio_ventilacao_w
from	mdc_episodio_pac_resp_mec
where	nr_sequencia	= nr_sequencia_p;

if (dt_inicio_ventilacao_w > dt_fim_p) then
	ds_erro_w := obter_desc_expressao(508988);--Data inicial não pode ser posterior à data final.	
end if;

if (ds_erro_w = 'S') then --Indica que não tem erro.
	qt_horas_total_w    := obter_hora_entre_datas(dt_inicio_ventilacao_w, dt_fim_p);

	update	mdc_episodio_pac_resp_mec
	set	    dt_fim_ventilacao	= dt_fim_p,
            nm_usuario		    = nm_usuario_p,
            dt_atualizacao		= clock_timestamp(),
            qt_horas_total      = qt_horas_total_w
	where	nr_sequencia		= nr_sequencia_p;
	
	commit;

else
	ds_erro_p := ds_erro_w;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
