CREATE OR REPLACE PROCEDURE mdc_duplicar_regra_ops ( nm_usuario_p mdc_regra_proc_agrup.nm_usuario%type, nr_sequencia_p mdc_regra_proc_agrup.nr_sequencia%type) AS $body$
DECLARE

-- ## Variaveis e cursores
mdc_regra_proc_agrup_w          mdc_regra_proc_agrup%rowtype;
nr_sequencia_w                  mdc_regra_proc_agrup.nr_sequencia%type;
nr_sequencia_item_w             mdc_regra_proc_agrup_item.nr_sequencia%type;

c_mdc_regra_proc_agrup_item CURSOR FOR
SELECT	*
from	mdc_regra_proc_agrup_item
where	nr_seq_regra = nr_sequencia_p;

-- ## Inicio da procedure
BEGIN

begin
select  *
into STRICT    mdc_regra_proc_agrup_w
from    mdc_regra_proc_agrup
where   nr_sequencia = nr_sequencia_p;
exception
when others then
	mdc_regra_proc_agrup_w.nr_sequencia := null;
end;

-- Regra
if (mdc_regra_proc_agrup_w.nr_sequencia IS NOT NULL AND mdc_regra_proc_agrup_w.nr_sequencia::text <> '')then

    select  nextval('mdc_regra_proc_agrup_seq')
    into STRICT    nr_sequencia_w
;

    mdc_regra_proc_agrup_w.nr_sequencia             := nr_sequencia_w;
    mdc_regra_proc_agrup_w.dt_atualizacao           := clock_timestamp();
    mdc_regra_proc_agrup_w.nm_usuario               := nm_usuario_p;
    mdc_regra_proc_agrup_w.dt_atualizacao_nrec      := clock_timestamp();
    mdc_regra_proc_agrup_w.nm_usuario_nrec          := nm_usuario_p;

    insert into mdc_regra_proc_agrup values (mdc_regra_proc_agrup_w.*);

    -- Criterios
    for mdc_regra_proc_agrup_item_w in c_mdc_regra_proc_agrup_item loop
        select  nextval('mdc_regra_proc_agrup_item_seq')
        into STRICT    nr_sequencia_item_w
;

        mdc_regra_proc_agrup_item_w.nr_sequencia            := nr_sequencia_item_w;
        mdc_regra_proc_agrup_item_w.dt_atualizacao          := clock_timestamp();
        mdc_regra_proc_agrup_item_w.nm_usuario              := nm_usuario_p;
        mdc_regra_proc_agrup_item_w.dt_atualizacao_nrec     := clock_timestamp();
        mdc_regra_proc_agrup_item_w.nm_usuario_nrec         := nm_usuario_p;
        mdc_regra_proc_agrup_item_w.nr_seq_regra            := nr_sequencia_w;

        insert into mdc_regra_proc_agrup_item values (mdc_regra_proc_agrup_item_w.*);
	end loop;

    commit;

