SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE md_copia_observacao_anterior (nr_sequencia_p bigint) AS $body$
DECLARE


ds_observacao_w		varchar(2000);
dt_dieta_w		timestamp;
dt_dieta_ref_w		timestamp;
cd_refeicao_w		varchar(02);
cd_refeicao_ref_w	varchar(02);
cd_pessoa_fisica_w	varchar(10);
ds_tipo_dieta_w		varchar(10);
ie_existe_reg_data_w	varchar(1);
i			integer := 1;
qt_count		integer := 0;
cd_dieta_w		mapa_dieta.cd_dieta%type;


BEGIN

ds_tipo_dieta_w	:= 'XRCJTABLMD';

select	max(a.dt_dieta),
	max(cd_pessoa_fisica),
	max(cd_refeicao),
	max(cd_dieta)
into STRICT	dt_dieta_w,
	cd_pessoa_fisica_w,
	cd_refeicao_w,
	cd_dieta_w
from	mapa_dieta a
where	a.nr_sequencia = nr_Sequencia_p;

cd_refeicao_ref_w 	:= cd_refeicao_w;
dt_dieta_ref_w		:= dt_dieta_w;

begin
select	'S'
into STRICT	ie_existe_reg_data_w
from	mapa_dieta a
where	coalesce(a.nr_Seq_superior::text, '') = ''
and	a.dt_dieta = dt_dieta_w
and	a.nr_sequencia <> nr_sequencia_p
and	a.cd_refeicao <> cd_refeicao_ref_w
and	a.cD_pessoa_fisica = cd_pessoa_fisica_w
--and	a.cd_refeicao <> cd_refeicao_w
and	(a.ds_observacao IS NOT NULL AND a.ds_observacao::text <> '')
and	substr(ds_tipo_dieta_w,position(cd_refeicao_ref_w in ds_tipo_dieta_w),length(ds_tipo_dieta_w)) like('%'||a.cd_refeicao||'%')  LIMIT 1;
exception
when others then
	ie_existe_reg_data_w	:= 'N';
end;

if (ie_existe_reg_data_w <> 'S') then
	cd_refeicao_ref_w	:= 'X';
	select	max(a.dt_dieta)
	into STRICT	dt_dieta_ref_w
	from	mapa_dieta a
	where	coalesce(a.nr_seq_superior::text, '') = ''
	and	a.cd_pessoa_fisica = cd_pessoa_fisica_w
	and	a.nr_sequencia <> nr_sequencia_p
	and	a.dt_dieta < dt_dieta_w
	and	(a.ds_observacao IS NOT NULL AND a.ds_observacao::text <> '');

	ds_tipo_dieta_w	:= substr(ds_tipo_dieta_w,position(cd_refeicao_ref_w in ds_tipo_dieta_w),length(ds_tipo_dieta_w));
else
	ds_tipo_dieta_w	:= substr(ds_tipo_dieta_w,position(cd_refeicao_ref_w in ds_tipo_dieta_w)+1,length(ds_tipo_dieta_w));
end if;

while(i <= length(ds_tipo_dieta_w)) and (coalesce(ds_observacao_w::text, '') = '') and (dt_dieta_ref_w IS NOT NULL AND dt_dieta_ref_w::text <> '') and (qt_count <= 10) loop
	begin

	cd_refeicao_ref_w := substr(ds_tipo_dieta_w,i,1);

	select	max(a.ds_observacao)
	into STRICT	ds_observacao_w
	from	mapa_dieta a
	where	a.dt_dieta = dt_dieta_ref_w
	and	a.nr_sequencia <> nr_sequencia_p
	and	a.cd_refeicao = cd_refeicao_ref_w
	and	a.cd_pessoa_fisica = cd_pessoa_fisica_w
	and	a.cd_dieta = cd_dieta_w;

	if (coalesce(ds_observacao_w::text, '') = '') then
		select	max(a.ds_observacao)
		into STRICT	ds_observacao_w
		from	mapa_dieta a
		where	a.dt_dieta = dt_dieta_ref_w
		and	a.nr_sequencia <> nr_sequencia_p
		and	a.cd_refeicao = cd_refeicao_ref_w
		and	a.cd_pessoa_fisica = cd_pessoa_fisica_w;
	end if;

	i 		:= i+1;
	qt_count	:= qt_count + 1;
	end;
end loop;

if (ds_observacao_w IS NOT NULL AND ds_observacao_w::text <> '') then
	update	mapa_dieta
	set	ds_observacao = ds_observacao_w
	where	nr_sequencia = nr_sequencia_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE md_copia_observacao_anterior (nr_sequencia_p bigint) FROM PUBLIC;

