CREATE OR REPLACE PROCEDURE medreceita_auditoria_json_atx ( nr_seq_med_receita_p med_receita_aud.nr_seq_med_receita%type, ds_json_p med_receita_aud.ds_json%type) AS $body$
DECLARE
nr_sequencia_w  med_receita_aud.nr_sequencia%type;
ie_acao_w varchar(1);


BEGIN

	select max(nr_sequencia), CASE WHEN coalesce(max(nr_sequencia), 0)=0 THEN  'I'  ELSE 'U' END
	into STRICT nr_sequencia_w, ie_acao_w
	from med_receita_aud 
	where nr_seq_med_receita = nr_seq_med_receita_p;
	
	if ie_acao_w = 'I' then
	begin
		select   nextval('med_receita_aud_seq')
		into STRICT     nr_sequencia_w
		;
		
		insert into med_receita_aud(
		  nr_sequencia,
		  nr_seq_med_receita, 
		  dt_atualizacao, 
		  nm_usuario, 
		  dt_atualizacao_nrec, 
		  nm_usuario_nrec,
		  ds_json
		)
		values ( 
		  nr_sequencia_w,
		  nr_seq_med_receita_p,
		  clock_timestamp(),
		  wheb_usuario_pck.get_nm_usuario,
		  clock_timestamp(),
		  wheb_usuario_pck.get_nm_usuario,
		  ds_json_p
		);
		
		commit;
	end;
	
	elsif ie_acao_w = 'U' then
	begin
		update  med_receita_aud
		set 	ds_json        = ds_json_p,
			nm_usuario     = wheb_usuario_pck.get_nm_usuario,
			dt_atualizacao = clock_timestamp()
		where 	nr_sequencia = nr_sequencia_w;

		commit;
	end;
	end if;


end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
